mapper.update:
UPDATE setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image},
WHERE id = #{id}

mapper.delete:
delete from setmeal
where id in

    #{id}

mapper.select:
select t1.*, t2.name as category_name from setmeal t1 left join category t2 on t1.category_id = t2.id
and category_id = #{categoryId}
and name like CONCAT('%', #{name}, '%')
and status = #{status}
